-- Create reference table for licences and accreditations if it doesn't exist
CREATE TABLE IF NOT EXISTS public.licences_accreditations_reference (
  id BIGSERIAL PRIMARY KEY,
  name TEXT NOT NULL,
  category TEXT NOT NULL,
  type TEXT NOT NULL CHECK (type IN ('Licence', 'Registration', 'Accreditation', 'Certification', 'Card', 'Clearance', 'Permit')),
  jurisdiction TEXT NOT NULL,
  description TEXT,
  issuing_authority TEXT,
  is_active BOOLEAN DEFAULT true,
  created_at TIMESTAMPTZ DEFAULT now(),
  updated_at TIMESTAMPTZ DEFAULT now(),
  
  -- Indexes for search/autocomplete
  CONSTRAINT unique_licence UNIQUE(name, jurisdiction)
);

CREATE INDEX IF NOT EXISTS idx_licences_category ON public.licences_accreditations_reference(category);
CREATE INDEX IF NOT EXISTS idx_licences_type ON public.licences_accreditations_reference(type);
CREATE INDEX IF NOT EXISTS idx_licences_jurisdiction ON public.licences_accreditations_reference(jurisdiction);
CREATE INDEX IF NOT EXISTS idx_licences_name_search ON public.licences_accreditations_reference USING gin(to_tsvector('english', name));

-- Clear existing data if re-running
TRUNCATE TABLE public.licences_accreditations_reference CASCADE;

